import java.util.Scanner;public class Solution {    public static void main(String[] args) {        Scanner sc = new Scanner(System.in);        ContactManager cm = new ContactManager(100);        String[] input;        do{            String  s = sc.nextLine();            input = s.split(" ");            if (input[0].equals("add")){                Contact c = new Contact(input[1], Long.parseLong(input[2]), input[3]);                cm.add(c);            }else if (input[0].equals("search")){                int inx = cm.search(input[1]);                System.out.println(inx);            }else if (input[0].equals("delete")){                boolean flag = cm.delete(input[1]);                System.out.println(flag);            }else if (input[0].equals("display")){              System.out.println(cm);            }          }while (!input[0].equals("end"));            }}class Contact {    String name;    long phoneNumber;    String email;    public Contact(String name, long phoneNumber, String email){        this.name = name;        this.phoneNumber = phoneNumber;        this.email= email;    }    public String toString(){        return this.name + " " + this.phoneNumber + " "+ this.email;    }}class ContactManager{    Contact[] contactList;    int index;    public ContactManager(int size){        this.contactList = new Contact[size];        this.index = 0;    }    public void add(Contact c){        this.contactList[index] = c;        this.index++;    }    public int search(String name){        for(int i = 0; i < index; i++){            if (name.equals(contactList[i].name)){                return i;            }        }return -1;    }        public String toString(){        String s = "";        for(int i = 0; i < this.index-1; i++){            s += contactList[i] + "\n";        }        return s+contactList[index-1];    }    public boolean delete(String name){        int index = search(name);        if (index == -1){            return false;        }        else{            for(int i = index; i < this.index - 1; i++){                this.contactList[i] = this.contactList[i + 1];            }            contactList[this.index] = null;            this.index--;            return true;        }    }}